def sort_shirt_sizes(sizes):
    """
    Sorts a list of shirt sizes (e.g., 'S', 'M', 'L', 'XS', 'XXL') 
    from smallest to largest.
    """
    # Helper function to convert size strings to a numeric value for comparison
    def size_to_value(size):
        if size == 'M':
            return 0

        # Count the number of 'X's to determine magnitude
        x_count = size.count('X')

        # For 'S' (Small), more 'X's means a smaller value (more negative)
        if size.endswith('S'):
            return -(x_count + 1)

        # For 'L' (Large), more 'X's means a larger value (more positive)
        if size.endswith('L'):
            return x_count + 1

        return 0

    # Sort the list using the custom key
    return sorted(sizes, key=size_to_value)
